mapper.select:
select afs.id,afs.theme,afs.end_date,co.fullname as contacts_id,div1.value as return_state,div2.value as
return_priority,u1.name as
owner
from tbl_after_sale afs
join tbl_contacts co on co.id=afs.contacts_id
left join tbl_dic_value div1 on div1.id=afs.return_state
left join tbl_dic_value div2 on div2.id=afs.return_priority
join tbl_user u1 on u1.id=co.owner


    and u1.id=#{owner}


    and afs.theme like '%' #{theme} '%'


    and afs.endDate=#{endDate}


    and co.fullname like '%' #{contactsName} '%'


    and afs.return_state=#{returnState}


    and afs.return_priority=#{returnPriority}


order by afs.create_time desc
limit #{pageNo},#{pageSize}

select count(*)
from tbl_after_sale afs
left join tbl_contacts co on co.id=afs.contacts_id
left join tbl_dic_value div1 on div1.id=afs.return_state
left join tbl_dic_value div2 on div2.id=afs.return_priority
join tbl_user u1 on u1.id=co.owner


    and u1.id=#{owner}


    and afs.theme like '%' #{theme} '%'


    and afs.end_date<=#{endDate}


    and co.fullname like '%' #{contactsName} '%'


    and afs.return_state=#{returnState}


    and afs.return_priority=#{returnPriority}
